import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  form: any = {
    data: {},
    message: '',
  }

  constructor(private httpClient:HttpClient , public router:Router) {

  }

  signIn() {
    this.httpClient.post('http://localhost:8000/ORSAPI/login/',this.form.data).subscribe((res:any)=>{
      console.log('res=>',res)
      this.form.message='';

      if(res.result.message){
        this.form.message=res.result.message;
      }

      if(res.data !=null){
        localStorage.setItem('firstName',res.result.data.firstName)
        this.router.navigateByUrl('welcome');
      }
      console.log('data',res.result.data)
      console.log('LocalStorage:', localStorage.getItem('firstName'));

    })
  }

  signUp() {
    this.router.navigateByUrl('signup')
  }

}
<app-navbar></app-navbar>

<div align="center" style="margin-top: 40px;">
    <h1>Login</h1>
    <h3>
        <font color="red">{{ form.message }}</font>
    </h3>

    <table cellpadding="10" cellspacing="0" style="border-collapse: separate; border-spacing: 10px;">
        <tr>
            <th align="right">Login ID:</th>
            <td><input type="text" name="loginId" [(ngModel)]="form.data.loginId" style="width: 250px;"></td>
        </tr>
        <tr>
            <th align="right">Password:</th>
            <td><input type="password" name="password" [(ngModel)]="form.data.password" style="width: 250px;"></td>
        </tr>
        <tr>
            <td colspan="2" align="center">
                <button (click)="signIn()">Sign In</button> &nbsp;
                <button (click)="signUp()">Sign Up</button>
            </td>
        </tr>
    </table>
</div>
